package sign

import "testing"

func TestSign(t *testing.T) {
	rsa2 := &RSA2{}
	sign := rsa2.Sign("app_id=2016080300154586&biz_content={\"out_trade_no\":\"201911270908381158722\",\"total_amount\":\"88.88\",\"subject\":\"iPhone Xs Max 256G\",\"buyer_id\":\"2088102175953034\"}&charset=utf-8&format=json&method=alipay.trade.create&sign_type=RSA2&timestamp=2021-04-22 22:51:34&version=1.0", "")
	if sign != "" {
		t.Errorf("RSA2于阿里签名不符合")
	}
}
